import React, { Component } from 'react';
import './subwork.css';

export default class Subwork extends Component {
    constructor(props)
    {
        super(props);

            this.state={
                isVisible:false,
            };

    }
   toggleVisibility=()=>{
    this.setState((prevState)=>({isVisible:!this.prevState}));
    if(this.state.isVisible)
    {
    document.querySelector('subworkBtn').innerHTML="See Less";
    }

   }
  render() {
    return (
      <div className='subwork'>        
         <div className={`transition-element ${this.state.isVisible ? 'visible' : ''}`}>
          This element will transition
        </div>
         <button className='subworkBtn' onClick={this.toggleVisibility}>See More</button>

      </div>
    )
  }
}
