import React from 'react';
import './navbar.css';
import logo from '../../assets/Images/logo.jpeg';
import contactImg from '../../assets/Images/contact.png';
import { Link } from 'react-scroll';

// Define toggleColor as a global function
function toggleColor(element) {
  const dots = document.querySelectorAll(".dot");

  for (const dot of dots) {
    dot.classList.remove("highlighted");
  }

  element.classList.add("highlighted");
}

const Navbar = () => {
  return (
    <nav className="navbar">
      <img className="logo" src={logo} alt="" style={{ width: "5rem" }} />

      <div className="navcenter">
        <Link
          activeClass="active"
          to="intro"
          spy={true}
          smooth={true}
          offset={-100}
          duration={500}
          className="navitems"
          onClick={() => toggleColor(document.querySelector(".dot.Home"))}
        >
          Home
        </Link>
        <Link
          activeClass="active"
          to="skills"
          spy={true}
          smooth={true}
          offset={-100}
          duration={500}
          className="navitems"
          onClick={() => toggleColor(document.querySelector(".dot.About"))}
        >
          About
        </Link>
        <Link
          activeClass="active"
          to="works"
          spy={true}
          smooth={true}
          offset={-100}
          duration={500}
          className="navitems"
          onClick={() => toggleColor(document.querySelector(".dot.Portfolio"))}
        >
          Portfolio
        </Link>
        <Link
          activeClass="active"
          to="contact"
          spy={true}
          smooth={true}
          offset={-100}
          duration={500}
          className="navitems"
          onClick={() => toggleColor(document.querySelector(".dot.Clients"))}
        >
          Clients
        </Link>
      </div>
      <button
        className="navcontact "
        onClick={() => {
          document.getElementById('contact').scrollIntoView({ behavior: "smooth" });
        }}
      >
        <img src={contactImg} className="navbarcontactimg" alt="" />
        Contact Me
      </button>
    </nav>
  );
}

export default Navbar;
