import React from 'react';
import './intro.css';
import bg from '../../assests/Images/Nishanth.png';
import btnImg from '../../assests/Images/hireme.png';
import {Link} from 'react-scroll';

export function changeNamecolor(e){
  return(

    e.target.style.color="white"

  )
}


const Intro=()=> {
  return (
    <section id="intro">
        <div className="introContent">
            <span className='Hello'>Hello,</span>
            <span className='Introtrext'>I'm <span className="introName" onMouseOver={changeNamecolor} onMouseOut={changeNamecolor}>Nishanth</span> <br />
            Website Designer</span>
            <p className="introPara">I am a skilled web designer with experience in creating visually apealing and user friendly websites.</p>
            <Link><button className="btn"><img src={btnImg} alt="" />Hire me</button></Link>
        </div>

        <img src={bg} alt="" className="bg" />
    </section>
  )
}

export default Intro;
