import React from 'react';
import './navbar.css';
import logo from '../../assests/Images/logo.jpeg';
import contactImg from '../../assests/Images/contact.png';
import { Link } from 'react-scroll';

const Navbar = () => {
  return (
    <nav className="navbar">
      <img className="logo" src={logo} alt="" style={{ width: "5rem" }} />
      <div className="navcenter">
        <Link activeClass="active" to="intro" spy={true} smooth={true} offset={-100} duration={500} className="navitems" onClick={() => toggleColor(document.querySelector(".dot.Home"), 'intro')}>Home</Link>
        <Link activeClass="active" to="skills" spy={true} smooth={true} offset={-100} duration={500} className="navitems" onClick={() => toggleColor(document.querySelector(".dot.About"), 'skills')}>About</Link>
        <Link activeClass="active" to="works" spy={true} smooth={true} offset={-100} duration={500} className="navitems" onClick={() => toggleColor(document.querySelector(".dot.Portfolio"), 'works')}>Portfolio</Link>
        <Link activeClass="active" to="contact" spy={true} smooth={true} offset={-100} duration={500} className="navitems" onClick={() => toggleColor(document.querySelector(".dot.Clients"), 'contact')}>Clients</Link>
      </div>
      <button className="navcontact" onClick={() => {
        document.getElementById('contact').scrollIntoView({ behavior: "smooth" });
      }}>
        <img src={contactImg} className="navbarcontactimg" alt="" />
        Contact Me
      </button>
    </nav>
  );
}

function toggleColor(element, className) {
  const target = document.getElementById(className);
  if (element.classList.contains("highlighted")) {
    element.classList.remove("highlighted");
    target.scrollIntoView({ behavior: "smooth" });
  } else {
    const highlightedDots = document.querySelectorAll(".dot.highlighted");
    for (const dot of highlightedDots) {
      dot.classList.remove("highlighted");
    }
    element.classList.add("highlighted");
    target.scrollIntoView({ behavior: "smooth" });
  }
}

export default Navbar;
